import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faEnvelope, faPaperPlane } from '@fortawesome/free-solid-svg-icons';
import axios from 'axios';

const ContactForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:3001/send-email', formData);
      alert('Message sent successfully!');
    } catch (error) {
      console.error('Error sending email:', error);
      alert('Failed to send message.');
    }
  };

  return (
    <div className='Contacti'>
    <div className="contact-form">
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <FontAwesomeIcon icon={faUser} className="icon-user" />
          <input type="text" name="name" id="name" placeholder="Name" onChange={handleChange} value={formData.name} required />
        </div>
        <div className="form-group">
          <FontAwesomeIcon icon={faEnvelope} className="icon-email" />
          <input type="email" name="email" id="email" placeholder="Email" onChange={handleChange} value={formData.email} required />
        </div>
        <div className="form-group">
          <textarea name="message" id="message" placeholder="Message..." onChange={handleChange} value={formData.message} required></textarea>
          <FontAwesomeIcon icon={faPaperPlane} className="icon-message" />
        </div>
        <button type="submit" className="send-button">
          Send Message
          <FontAwesomeIcon icon={faPaperPlane} />
        </button>
      </form>
    </div>
    </div>
  );
};

export default ContactForm;
